
public class 삼항연산자_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=10;
		System.out.printf("%d\n",num1); // 10
		num1++;
		System.out.printf("%d\n",num1); // 11
		++num1;
		System.out.printf("%d\n",num1); // 12
		--num1;
		System.out.printf("%d\n",num1); // 11
		num1--;
		System.out.printf("%d\n",num1); // 11
	
	}

}
