/*
 * 첫번째 결과 : 2
 * 두번째 결과 : 1
 * 
 * 첫번째 결과 : 2
 * 두번째 결과 : 1
 * 
 * 
 */
public class 문재02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		
		System.out.println("첫번째 결과 :"+ i++ *2);
		System.out.println("두번째 결과:"+i);
		
		i=0;
		System.out.println("첫번째 결과:"+ ++i*2);
		System.out.println("두번째 결과:"+i);
	}

}
