public class 제어문_반복문_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("========== for ============");
		for(int i=1;i<=10;i++)
		{
			System.out.print(i+" ");
		}
		
		System.out.println("========== while ============");
		
		int i=1;
		while(i<=10)
		{
			System.out.print(i+" ");
			i++;
		}
	}

}
